import React, { useState, useEffect } from 'react';
import { documentService } from '../services/api';

const DocumentList = ({ courseId }) => {
  const [documents, setDocuments] = useState([]);
  const [loading, setLoading] = useState(true);
  const [summaries, setSummaries] = useState({});
  const [summarizing, setSummarizing] = useState({});

  useEffect(() => {
    fetchDocuments();
  }, [courseId]);

  const fetchDocuments = async () => {
    try {
      const response = await documentService.getDocumentsByCourse(courseId);
      setDocuments(response.data);
    } catch (error) {
      console.error('Error fetching documents:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleSummarize = async (docId) => {
    setSummarizing(prev => ({ ...prev, [docId]: true }));
    try {
      const response = await documentService.summarizeDocument(docId);
      setSummaries(prev => ({ ...prev, [docId]: response.data.summary }));
    } catch (error) {
      alert('Failed to summarize: ' + error.message);
    } finally {
      setSummarizing(prev => ({ ...prev, [docId]: false }));
    }
  };

  const handleViewDocument = async (docId, fileName, fileType) => {
    try {
      const response = await documentService.viewDocument(docId);
      // Create a blob URL from the response
      const blob = new Blob([response.data], { type: fileType || 'application/pdf' });
      const url = window.URL.createObjectURL(blob);
      // Open in new tab
      window.open(url, '_blank');
      // Clean up the URL after a delay (optional)
      setTimeout(() => window.URL.revokeObjectURL(url), 100);
    } catch (error) {
      alert('Failed to open document: ' + (error.response?.data?.message || error.message));
    }
  };

  const handleDownloadDocument = async (docId, fileName, fileType) => {
    try {
      const response = await documentService.downloadDocument(docId);
      // Create a blob URL from the response
      const blob = new Blob([response.data], { type: fileType || 'application/pdf' });
      const url = window.URL.createObjectURL(blob);
      // Create a temporary link and trigger download
      const link = document.createElement('a');
      link.href = url;
      link.download = fileName;
      document.body.appendChild(link);
      link.click();
      document.body.removeChild(link);
      // Clean up the URL
      setTimeout(() => window.URL.revokeObjectURL(url), 100);
    } catch (error) {
      alert('Failed to download document: ' + (error.response?.data?.message || error.message));
    }
  };

  if (loading) {
    return (
      <div className="text-center py-5">
        <div className="spinner-border"></div>
      </div>
    );
  }

  return (
    <div>
      <h5 className="mb-4">
        <i className="bi bi-file-text"></i> Course Documents
      </h5>
      {documents.length === 0 ? (
        <div className="alert alert-info">No documents available</div>
      ) : (
        documents.map(doc => (
          <div key={doc._id} className="card mb-3">
            <div className="card-body">
              <div className="d-flex justify-content-between align-items-start">
                <div className="flex-grow-1">
                  <h6 
                    className="card-title" 
                    style={{ cursor: 'pointer', color: '#0d6efd' }}
                    onClick={() => handleViewDocument(doc._id, doc.fileName, doc.fileType)}
                    title="Click to open document"
                  >
                    <i className="bi bi-file-earmark-pdf"></i> {doc.title}
                  </h6>
                  <p className="card-text text-muted small">{doc.description}</p>
                  <small className="text-muted">
                    <i className="bi bi-file"></i> {doc.fileName} | {(doc.fileSize / 1024).toFixed(2)} KB
                  </small>
                </div>
                <div className="ms-2">
                  <button
                    className="btn btn-sm btn-primary me-2"
                    onClick={() => handleViewDocument(doc._id, doc.fileName, doc.fileType)}
                  >
                    <i className="bi bi-eye"></i> View
                  </button>
                  <button
                    className="btn btn-sm btn-info me-2"
                    onClick={() => handleDownloadDocument(doc._id, doc.fileName, doc.fileType)}
                  >
                    <i className="bi bi-download"></i> Download
                  </button>
                  <button
                    className="btn btn-sm btn-success"
                    onClick={() => handleSummarize(doc._id)}
                    disabled={summarizing[doc._id]}
                  >
                    <i className="bi bi-magic"></i> {summarizing[doc._id] ? 'Summarizing...' : 'Summarize'}
                  </button>
                </div>
              </div>
              {summaries[doc._id] && (
                <div className="mt-3 p-3 bg-light rounded">
                  <strong>AI Summary:</strong>
                  <p className="mt-2 mb-0">{summaries[doc._id]}</p>
                </div>
              )}
            </div>
          </div>
        ))
      )}
    </div>
  );
};

export default DocumentList;
