import React, { useState, useEffect } from 'react';
import { courseService } from '../services/api';
import VideoPlayer from './VideoPlayer';

const CourseSections = ({ courseId }) => {
  const [course, setCourse] = useState(null);
  const [expandedSections, setExpandedSections] = useState({});
  const [loading, setLoading] = useState(true);
  const [selectedLecture, setSelectedLecture] = useState(null);

  useEffect(() => {
    const fetchCourse = async () => {
      try {
        const response = await courseService.getCourseById(courseId);
        setCourse(response.data);
        setLoading(false);
      } catch (error) {
        console.error('Error fetching course:', error);
        setLoading(false);
      }
    };
    fetchCourse();
  }, [courseId]);

  const toggleSection = (sectionId) => {
    setExpandedSections(prev => ({
      ...prev,
      [sectionId]: !prev[sectionId]
    }));
  };

  const handleLectureClick = async (lecture, courseId, lectureId) => {
    setSelectedLecture(lecture);
    try {
      await courseService.markLectureComplete(courseId, lectureId);
    } catch (error) {
      console.error('Error marking lecture complete:', error);
    }
  };

  if (loading) {
    return <div className="text-center py-5"><div className="spinner-border text-primary"></div></div>;
  }

  if (!course) {
    return <div className="alert alert-danger">Course not found</div>;
  }

  return (
    <div className="course-sections">
      <div className="course-header mb-4 p-4 bg-light rounded">
        <h1 className="mb-2">{course.title}</h1>
        <p className="text-muted mb-2">{course.description}</p>
        <div className="d-flex gap-3">
          <span className="badge bg-success">Status: Completed</span>
          <span className="text-muted">Instructor: {course.instructor || 'N/A'}</span>
        </div>
      </div>

      <div className="sections-container">
        {course.sections && course.sections.map((section, index) => (
          <div key={section._id} className="card mb-3">
            <div 
              className="card-header bg-light cursor-pointer d-flex justify-content-between align-items-center"
              onClick={() => toggleSection(section._id)}
              style={{ cursor: 'pointer' }}
            >
              <div>
                <h5 className="mb-0">
                  <i className="bi bi-folder"></i> {index + 1}. {section.title}
                </h5>
                <small className="text-muted">
                  {section.lectures?.length || 0} Lectures
                  {section.quiz && ', 1 Quiz'}
                </small>
              </div>
              <span className="text-primary">
                <i className={`bi ${expandedSections[section._id] ? 'bi-dash' : 'bi-plus'}`}></i>
              </span>
            </div>

            {expandedSections[section._id] && (
              <div className="card-body">
                {section.lectures && section.lectures.map((lecture, lectureIndex) => (
                  <div 
                    key={lecture._id} 
                    className="lecture-item p-3 mb-2 border rounded d-flex align-items-center"
                    style={{ backgroundColor: '#f9f9f9' }}
                  >
                    <i className="bi bi-play-circle text-primary me-3" style={{ fontSize: '1.5rem' }}></i>
                    <div className="flex-grow-1">
                      <h6 className="mb-1">{lectureIndex + 1}. {lecture.title}</h6>
                      <small className="text-muted">{lecture.description}</small>
                    </div>
                    <button 
                      className="btn btn-sm btn-outline-primary"
                      onClick={() => handleLectureClick(lecture, courseId, lecture._id)}
                    >
                      <i className="bi bi-play"></i> Watch
                    </button>
                  </div>
                ))}

                {section.quiz && (
                  <div className="quiz-item p-3 mb-2 border rounded d-flex align-items-center" style={{ backgroundColor: '#f0f8ff' }}>
                    <i className="bi bi-question-circle text-warning me-3" style={{ fontSize: '1.5rem' }}></i>
                    <div className="flex-grow-1">
                      <h6 className="mb-1">{section.quiz.title}</h6>
                      <small className="text-muted">{section.quiz.questions} Questions</small>
                    </div>
                    <button className="btn btn-sm btn-outline-warning">
                      <i className="bi bi-pencil"></i> Attempt
                    </button>
                  </div>
                )}
              </div>
            )}
          </div>
        ))}
      </div>

      {selectedLecture && (
        <VideoPlayer 
          lecture={selectedLecture} 
          onClose={() => setSelectedLecture(null)}
        />
      )}
    </div>
  );
};

export default CourseSections;
