import React, { useState, useEffect, useContext } from 'react';
import { useNavigate } from 'react-router-dom';
import { courseService } from '../services/api';
import { AuthContext } from '../context/AuthContext';

const Courses = () => {
  const [courses, setCourses] = useState([]);
  const [loading, setLoading] = useState(true);
  const { user } = useContext(AuthContext);
  const navigate = useNavigate();

  useEffect(() => {
    if (!user) {
      navigate('/login');
      return;
    }

    const fetchCourses = async () => {
      try {
        const response = await courseService.getAllCourses();
        setCourses(response.data);
        setLoading(false);
      } catch (error) {
        console.error('Error fetching courses:', error);
        setLoading(false);
      }
    };
    fetchCourses();
  }, [user, navigate]);

  const handleEnroll = async (courseId) => {
    try {
      await courseService.enrollCourse(courseId);
    } catch (error) {
      console.error('Error enrolling:', error);
    } finally {
      navigate(`/course/${courseId}`);
    }
  };

  if (loading) {
    return <div className="text-center py-5"><div className="spinner-border text-primary"></div></div>;
  }

  return (
    <div className="container my-5">
      <h2 className="mb-4">Available Courses</h2>
      {courses.length === 0 ? (
        <div className="alert alert-info">No courses available</div>
      ) : (
        <div className="row">
          {courses.map(course => (
            <div key={course._id} className="col-md-4 mb-4">
              <div className="card h-100">
                {course.thumbnail && (
                  <img src={course.thumbnail} className="card-img-top" alt={course.title} style={{ height: '200px', objectFit: 'cover' }} />
                )}
                <div className="card-body">
                  <h5 className="card-title">{course.title}</h5>
                  <p className="card-text text-muted">{course.description}</p>
                  <div className="d-flex justify-content-between align-items-center mb-3">
                    <small className="text-muted">
                      <i className="bi bi-book"></i> {course.totalLectures || 0} Lectures
                    </small>
                    <small className="text-muted">
                      <i className="bi bi-clock"></i> {course.totalDuration || 'N/A'} hrs
                    </small>
                  </div>
                  <button 
                    className="btn btn-primary w-100"
                    onClick={() => handleEnroll(course._id)}
                  >
                    <i className="bi bi-play-circle"></i> Start Course
                  </button>
                </div>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default Courses;
