import React, { useContext } from 'react';
import { useParams, useNavigate, Link } from 'react-router-dom';
import { AuthContext } from '../context/AuthContext';
import CourseSections from '../components/CourseSections';

const CourseDetail = () => {
  const { id } = useParams();
  const { user } = useContext(AuthContext);
  const navigate = useNavigate();

  if (!user) {
    navigate('/login');
    return null;
  }

  return (
    <div className="container-fluid">
      <div className="row">
        <nav className="col-md-2 bg-dark p-3" style={{ minHeight: '100vh' }}>
          <ul className="navbar-nav">
            <li className="nav-item mb-3">
              <Link to="/courses" className="nav-link text-light">
                <i className="bi bi-house"></i> My Courses
              </Link>
            </li>
            <li className="nav-item">
              <span className="nav-link text-muted small">Course Navigation</span>
            </li>
          </ul>
        </nav>
        <main className="col-md-10 p-4">
          <CourseSections courseId={id} />
        </main>
      </div>
    </div>
  );
};

export default CourseDetail;
