import React from 'react';
import { useNavigate } from 'react-router-dom';

const ProgramLanding = () => {
  const navigate = useNavigate();

  const handleApplyNow = () => {
    navigate('/courses');
  };

  const handleDownloadBrochure = () => {
    // Placeholder for download functionality
    alert('Brochure download feature coming soon!');
  };

  return (
    <div className="program-landing-page" style={{ minHeight: '100vh', backgroundColor: '#ffffff' }}>
      {/* Hero Section */}
      <div className="hero-section" style={{ 
        background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
        padding: '80px 0 60px',
        color: '#ffffff'
      }}>
        <div className="container">
          <div className="row align-items-center">
            <div className="col-lg-7">
              <div style={{ marginBottom: '15px' }}>
                <span style={{ 
                  backgroundColor: 'rgba(255, 255, 255, 0.25)',
                  padding: '8px 16px',
                  borderRadius: '25px',
                  fontSize: '13px',
                  fontWeight: '600',
                  display: 'inline-block',
                  letterSpacing: '0.5px'
                }}>
                  ‚úì Job Assurance
                </span>
              </div>
              <h1 style={{ 
                fontSize: '2.8rem',
                fontWeight: '700',
                marginBottom: '12px',
                lineHeight: '1.2',
                letterSpacing: '-0.5px'
              }}>
                MyCaptain Digital Marketing Program
              </h1>
              <p style={{ 
                fontSize: '1.15rem',
                marginBottom: '8px',
                opacity: 0.95,
                fontWeight: '400'
              }}>
                by <strong style={{ fontWeight: '600' }}>Imarticus Learning</strong>
              </p>
              <p style={{ 
                fontSize: '0.95rem',
                opacity: 0.85,
                marginBottom: '35px'
              }}>
                Designed for <strong style={{ fontWeight: '600' }}>Freshers & Early Working Professionals</strong>
              </p>
            </div>
            <div className="col-lg-5">
              <div style={{ 
                backgroundColor: 'rgba(255, 255, 255, 0.2)',
                backdropFilter: 'blur(10px)',
                borderRadius: '12px',
                padding: '35px',
                border: '1px solid rgba(255, 255, 255, 0.3)',
                boxShadow: '0 8px 32px rgba(0, 0, 0, 0.1)'
              }}>
                <div className="row text-center">
                  <div className="col-4" style={{ borderRight: '1px solid rgba(255, 255, 255, 0.2)' }}>
                    <div style={{ fontSize: '12px', opacity: 0.9, marginBottom: '8px', textTransform: 'uppercase', letterSpacing: '0.5px' }}>Duration</div>
                    <div style={{ fontSize: '26px', fontWeight: '700' }}>18</div>
                    <div style={{ fontSize: '11px', opacity: 0.8 }}>Weeks</div>
                  </div>
