import React from 'react';
import { useNavigate } from 'react-router-dom';

const ProgramLanding = () => {
  const navigate = useNavigate();

  const handleApplyNow = () => {
    navigate('/courses');
  };

  const handleDownloadBrochure = () => {
    // Placeholder for download functionality
    alert('Brochure download feature coming soon!');
  };

  return (
    <div className="program-landing-page" style={{ minHeight: '100vh', backgroundColor: '#ffffff' }}>
      {/* Hero Section */}
      <div className="hero-section" style={{ 
        background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
        padding: '60px 0',
        color: '#ffffff'
      }}>
        <div className="container">
          <div className="row align-items-center">
            <div className="col-lg-8">
              <div style={{ marginBottom: '20px' }}>
                <span style={{ 
                  backgroundColor: 'rgba(255, 255, 255, 0.2)',
                  padding: '6px 12px',
                  borderRadius: '20px',
                  fontSize: '14px',
                  fontWeight: '500',
                  display: 'inline-block',
                  marginBottom: '15px'
                }}>
                  üéØ Job Assurance
                </span>
              </div>
              <h1 style={{ 
                fontSize: '2.5rem',
                fontWeight: '700',
                marginBottom: '15px',
                lineHeight: '1.2'
              }}>
                MyCaptain Digital Marketing Program
              </h1>
              <p style={{ 
                fontSize: '1.2rem',
                marginBottom: '10px',
                opacity: 0.95
              }}>
                by <strong>Imarticus Learning</strong>
              </p>
              <p style={{ 
                fontSize: '1rem',
                opacity: 0.9,
                marginBottom: '30px'
              }}>
                Designed for <strong>Freshers & Early Working Professionals</strong>
              </p>
            </div>
            <div className="col-lg-4 text-center">
              <div style={{ 
                backgroundColor: 'rgba(255, 255, 255, 0.15)',
                backdropFilter: 'blur(10px)',
                borderRadius: '15px',
                padding: '30px',
                border: '1px solid rgba(255, 255, 255, 0.2)'
              }}>
                <div style={{ marginBottom: '20px' }}>
                  <div style={{ fontSize: '14px', opacity: 0.9, marginBottom: '5px' }}>Duration</div>
                  <div style={{ fontSize: '28px', fontWeight: '700' }}>18 Weeks</div>
                </div>
                <div style={{ marginBottom: '20px' }}>
                  <div style={{ fontSize: '14px', opacity: 0.9, marginBottom: '5px' }}>Next Batch</div>
                  <div style={{ fontSize: '24px', fontWeight: '600' }}>October</div>
                </div>
                <div>
                  <div style={{ fontSize: '14px', opacity: 0.9, marginBottom: '5px' }}>Available Seats</div>
                  <div style={{ fontSize: '20px', fontWeight: '600' }}>
                    <span style={{ color: '#ffd700' }}>29</span> / 60
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Experts Section */}
      <div className="experts-section" style={{ 
        padding: '40px 0',
        backgroundColor: '#f8f9fa'
      }}>
        <div className="container">
          <div className="text-center mb-4">
            <h3 style={{ 
              fontSize: '1.5rem',
              fontWeight: '600',
              color: '#333',
              marginBottom: '10px'
            }}>
              Learn from Industry Experts
            </h3>
            <p style={{ color: '#666', fontSize: '1rem' }}>
              Experts from leading companies
            </p>
          </div>
          <div className="row justify-content-center">
            {['Rapido', 'Deloitte', 'MFine', 'Zomato'].map((company, index) => (
              <div key={index} className="col-6 col-md-3 mb-3">
                <div style={{
                  backgroundColor: '#ffffff',
                  padding: '20px',
                  borderRadius: '10px',
                  textAlign: 'center',
                  boxShadow: '0 2px 8px rgba(0,0,0,0.1)',
                  transition: 'transform 0.3s ease',
                  cursor: 'pointer'
                }}
                onMouseEnter={(e) => e.currentTarget.style.transform = 'translateY(-5px)'}
                onMouseLeave={(e) => e.currentTarget.style.transform = 'translateY(0)'}
                >
                  <div style={{
                    fontSize: '1.2rem',
                    fontWeight: '600',
                    color: '#667eea'
                  }}>
                    {company}
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>

