      <div className="cta-section" style={{ 
        padding: '70px 0',
        backgroundColor: '#ffffff'
      }}>
        <div className="container">
          <div className="row justify-content-center">
            <div className="col-lg-10 text-center">
              <div className="d-flex gap-3 justify-content-center flex-wrap" style={{ marginTop: '20px' }}>
                <button
                  onClick={handleApplyNow}
                  className="btn-apply-now"
                  style={{
                    backgroundColor: '#667eea',
                    color: '#ffffff',
                    border: 'none',
                    padding: '16px 50px',
                    borderRadius: '8px',
                    fontSize: '1.05rem',
                    fontWeight: '600',
                    cursor: 'pointer',
                    transition: 'all 0.3s ease',
                    boxShadow: '0 4px 15px rgba(102, 126, 234, 0.4)',
                    letterSpacing: '0.3px'
                  }}
                  onMouseEnter={(e) => {
                    e.currentTarget.style.backgroundColor = '#5568d3';
                    e.currentTarget.style.transform = 'translateY(-2px)';
                    e.currentTarget.style.boxShadow = '0 6px 20px rgba(102, 126, 234, 0.5)';
                  }}
                  onMouseLeave={(e) => {
                    e.currentTarget.style.backgroundColor = '#667eea';
                    e.currentTarget.style.transform = 'translateY(0)';
                    e.currentTarget.style.boxShadow = '0 4px 15px rgba(102, 126, 234, 0.4)';
                  }}
                >
                  Apply Now
                </button>
                <button
                  onClick={handleDownloadBrochure}
                  className="btn-download-brochure"
                  style={{
                    backgroundColor: '#ffffff',
                    color: '#667eea',
                    border: '2px solid #667eea',
                    padding: '14px 50px',
                    borderRadius: '8px',
                    fontSize: '1.05rem',
                    fontWeight: '600',
                    cursor: 'pointer',
                    transition: 'all 0.3s ease',
                    letterSpacing: '0.3px'
                  }}
                  onMouseEnter={(e) => {
                    e.currentTarget.style.backgroundColor = '#f8f9fa';
                    e.currentTarget.style.transform = 'translateY(-2px)';
                    e.currentTarget.style.borderColor = '#5568d3';
                  }}
                  onMouseLeave={(e) => {
                    e.currentTarget.style.backgroundColor = '#ffffff';
                    e.currentTarget.style.transform = 'translateY(0)';
                    e.currentTarget.style.borderColor = '#667eea';
                  }}
                >
                  Download Brochure
                </button>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Features Section */}
      <div className="features-section" style={{ 
        padding: '60px 0',
        backgroundColor: '#f8f9fa'
      }}>
        <div className="container">
          <div className="row">
            <div className="col-md-4 mb-4">
              <div style={{
                backgroundColor: '#ffffff',
                padding: '30px',
                borderRadius: '10px',
                textAlign: 'center',
                height: '100%',
                boxShadow: '0 2px 8px rgba(0,0,0,0.1)'
              }}>
                <div style={{ fontSize: '2.5rem', marginBottom: '15px' }}>üìö</div>
                <h4 style={{ fontWeight: '600', marginBottom: '10px', color: '#333' }}>Comprehensive Curriculum</h4>
                <p style={{ color: '#666' }}>Learn all aspects of digital marketing from basics to advanced strategies</p>
              </div>
            </div>
            <div className="col-md-4 mb-4">
              <div style={{
                backgroundColor: '#ffffff',
                padding: '30px',
                borderRadius: '10px',
                textAlign: 'center',
                height: '100%',
                boxShadow: '0 2px 8px rgba(0,0,0,0.1)'
              }}>
                <div style={{ fontSize: '2.5rem', marginBottom: '15px' }}>üë®‚Äçüè´</div>
                <h4 style={{ fontWeight: '600', marginBottom: '10px', color: '#333' }}>Expert Mentors</h4>
                <p style={{ color: '#666' }}>Learn from industry professionals with real-world experience</p>
              </div>
            </div>
            <div className="col-md-4 mb-4">
              <div style={{
                backgroundColor: '#ffffff',
                padding: '30px',
                borderRadius: '10px',
                textAlign: 'center',
                height: '100%',
                boxShadow: '0 2px 8px rgba(0,0,0,0.1)'
              }}>
                <div style={{ fontSize: '2.5rem', marginBottom: '15px' }}>üíº</div>
                <h4 style={{ fontWeight: '600', marginBottom: '10px', color: '#333' }}>Job Assurance</h4>
                <p style={{ color: '#666' }}>Get placement assistance and career support after completion</p>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ProgramLanding;
