import React, { useState, useEffect, useContext } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { courseService } from '../services/api';
import { AuthContext } from '../context/AuthContext';
import DocumentList from '../components/DocumentList';
import './CourseContent.css';

const CourseContent = () => {
  const { id } = useParams();
  const { user } = useContext(AuthContext);
  const navigate = useNavigate();

  const [course, setCourse] = useState(null);
  const [loading, setLoading] = useState(true);
  const [expandedSections, setExpandedSections] = useState({});
  const [selectedLecture, setSelectedLecture] = useState(null);
  const [activeTab, setActiveTab] = useState('lectures');
  const [refreshDocs, setRefreshDocs] = useState(0);

  useEffect(() => {
    if (!user) {
      navigate('/login');
      return;
    }

    const fetchCourse = async () => {
      try {
        const response = await courseService.getCourseById(id);
        setCourse(response.data);
        if (response.data.sections && response.data.sections.length > 0) {
          setExpandedSections({ [response.data.sections[0]._id]: true });
        }
        setLoading(false);
      } catch (error) {
        console.error('Error fetching course:', error);
        setLoading(false);
      }
    };

    fetchCourse();
  }, [id, user, navigate]);

  const toggleSection = (sectionId) => {
    setExpandedSections(prev => ({
      ...prev,
      [sectionId]: !prev[sectionId]
    }));
  };

  const handleLectureClick = async (lecture) => {
    setSelectedLecture(lecture);
    try {
      await courseService.markLectureComplete(id, lecture._id);
    } catch (error) {
      console.error('Error marking lecture complete:', error);
    }
  };

  if (loading) {
    return (
      <div className="text-center py-5">
        <div className="spinner-border text-primary"></div>
        <p className="mt-3">Loading course content...</p>
      </div>
    );
  }

  if (!course) {
    return (
      <div className="container my-5">
        <div className="alert alert-danger">Course not found</div>
        <button className="btn btn-secondary" onClick={() => navigate('/courses')}>
          Back to Courses
        </button>
      </div>
    );
  }

  return (
    <div className="course-content-page">
      <div className="course-header bg-primary text-white py-5">
        <div className="container">
          <button 
            className="btn btn-light btn-sm mb-3"
            onClick={() => navigate('/courses')}
          >
            <i className="bi bi-arrow-left"></i> Back to Courses
          </button>
          <h1 className="mb-2">{course.title}</h1>
          <p className="lead mb-0">{course.description}</p>
          <div className="mt-3">
            <span className="badge bg-light text-dark me-2">
              <i className="bi bi-book"></i> {course.totalLectures || 0} Lectures
            </span>
            <span className="badge bg-light text-dark me-2">
              <i className="bi bi-clock"></i> {course.totalDuration || 'N/A'} hrs
            </span>
            <span className="badge bg-light text-dark">
              <i className="bi bi-person"></i> {course.instructor || 'Instructor'}
            </span>
          </div>
        </div>
      </div>

      <div className="container py-5">
        <div className="row">
          <div className="col-lg-3 mb-4">
            <div className="card">
              <div className="card-header bg-light">
                <h5 className="mb-0">Course Lessons</h5>
              </div>
              <div className="card-body p-0">
                <div className="lessons-sidebar">
                  {course.sections && course.sections.map((section, index) => (
                    <div key={section._id} className="lesson-item">
                      <button
                        className="lesson-toggle w-100 text-start p-3 border-0 bg-white"
                        onClick={() => toggleSection(section._id)}
                      >
                        <div className="d-flex justify-content-between align-items-center">
                          <div>
                            <div className="fw-bold">
                              <i className="bi bi-mortarboard"></i> Lesson {index + 1}
                            </div>
                            <small className="text-muted">{section.title}</small>
                          </div>
                          <i className={`bi ${expandedSections[section._id] ? 'bi-chevron-up' : 'bi-chevron-down'}`}></i>
                        </div>
                      </button>

                      {expandedSections[section._id] && (
                        <div className="lesson-content border-top">
                          {section.lectures && section.lectures.map((lecture, lectureIndex) => (
                            <button
                              key={lecture._id}
                              className="lecture-link w-100 text-start px-3 py-2 border-0 bg-white text-decoration-none"
                              onClick={() => handleLectureClick(lecture)}
                              style={{ 
                                paddingLeft: '2rem',
                                borderLeft: selectedLecture?._id === lecture._id ? '3px solid #0d6efd' : 'none',
                                backgroundColor: selectedLecture?._id === lecture._id ? '#f0f6ff' : 'white'
                              }}
                            >
                              <small>
                                <i className="bi bi-play-circle"></i> {lectureIndex + 1}. {lecture.title}
                              </small>
                            </button>
                          ))}
                          {section.quiz && (
                            <button
                              className="quiz-link w-100 text-start px-3 py-2 border-0 bg-light text-decoration-none"
                              style={{ paddingLeft: '2rem' }}
                            >
                              <small>
                                <i className="bi bi-question-circle"></i> Quiz: {section.quiz.title}
                              </small>
                            </button>
                          )}
                        </div>
                      )}
                    </div>
                  ))}
                </div>
              </div>
            </div>
          </div>

          <div className="col-lg-9">
            <div className="card mb-3">
              <div className="card-header bg-light border-bottom">
                <div className="btn-group w-100" role="tablist">
                  <button
                    className={`btn btn-light border ${activeTab === 'lectures' ? 'btn-primary' : ''}`}
                    onClick={() => setActiveTab('lectures')}
                  >
                    <i className="bi bi-play-circle"></i> Lectures
                  </button>
                  <button
                    className={`btn btn-light border ${activeTab === 'documents' ? 'btn-primary' : ''}`}
                    onClick={() => setActiveTab('documents')}
                  >
                    <i className="bi bi-file-text"></i> Documents
                  </button>
                </div>
              </div>
              <div className="card-body">
                {activeTab === 'lectures' && (
                  <>
                    {selectedLecture ? (
                      <div>
                        <h3 className="card-title mb-3">{selectedLecture.title}</h3>
                        {selectedLecture.videoUrl && (
                          <div className="video-container mb-4">
                            <iframe
                              width="100%"
                              height="500"
                              src={selectedLecture.videoUrl}
                              title={selectedLecture.title}
                              frameBorder="0"
                              allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
                              allowFullScreen
                            ></iframe>
                          </div>
                        )}
                        <p className="text-muted">{selectedLecture.description}</p>
                        <div className="alert alert-success" role="alert">
                          <i className="bi bi-check-circle"></i> Lecture marked as watched
                        </div>
                      </div>
                    ) : (
                      <div className="text-center py-5">
                        <i className="bi bi-play-circle" style={{ fontSize: '3rem', color: '#ccc' }}></i>
                        <h5 className="mt-3 text-muted">Select a lecture to watch</h5>
                        <p className="text-muted">Click on any lecture from the lessons list on the left</p>
                      </div>
                    )}
                  </>
                )}
                {activeTab === 'documents' && (
                  <DocumentList courseId={id} key={refreshDocs} />
                )}
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default CourseContent;
