import React, { useState, useEffect } from 'react';
import { documentService } from '../services/api';

const DocumentList = ({ courseId }) => {
  const [documents, setDocuments] = useState([]);
  const [loading, setLoading] = useState(true);
  const [summaries, setSummaries] = useState({});
  const [summarizing, setSummarizing] = useState({});

  useEffect(() => {
    fetchDocuments();
  }, [courseId]);

  const fetchDocuments = async () => {
    try {
      const response = await documentService.getDocumentsByCourse(courseId);
      setDocuments(response.data);
    } catch (error) {
      console.error('Error fetching documents:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleSummarize = async (docId) => {
    setSummarizing(prev => ({ ...prev, [docId]: true }));
    try {
      const response = await documentService.summarizeDocument(docId);
      setSummaries(prev => ({ ...prev, [docId]: response.data.summary }));
    } catch (error) {
      alert('Failed to summarize: ' + error.message);
    } finally {
      setSummarizing(prev => ({ ...prev, [docId]: false }));
    }
  };

  if (loading) {
    return (
      <div className="text-center py-5">
        <div className="spinner-border"></div>
      </div>
    );
  }

  return (
    <div>
      <h5 className="mb-4">
        <i className="bi bi-file-text"></i> Course Documents
      </h5>
      {documents.length === 0 ? (
        <div className="alert alert-info">No documents available</div>
      ) : (
        documents.map(doc => (
          <div key={doc._id} className="card mb-3">
            <div className="card-body">
              <div className="d-flex justify-content-between align-items-start">
                <div className="flex-grow-1">
                  <h6 className="card-title">{doc.title}</h6>
                  <p className="card-text text-muted small">{doc.description}</p>
                  <small className="text-muted">
                    <i className="bi bi-file"></i> {doc.fileName} | {(doc.fileSize / 1024).toFixed(2)} KB
                  </small>
                </div>
                <button
                  className="btn btn-sm btn-success ms-2"
                  onClick={() => handleSummarize(doc._id)}
                  disabled={summarizing[doc._id]}
                >
                  <i className="bi bi-magic"></i> {summarizing[doc._id] ? 'Summarizing...' : 'Summarize'}
                </button>
              </div>
              {summaries[doc._id] && (
                <div className="mt-3 p-3 bg-light rounded">
                  <strong>AI Summary:</strong>
                  <p className="mt-2 mb-0">{summaries[doc._id]}</p>
                </div>
              )}
            </div>
          </div>
        ))
      )}
    </div>
  );
};

export default DocumentList;
