import React, { useContext, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { AuthContext } from '../context/AuthContext';
import { courseService } from '../services/api';
import DocumentUpload from '../components/DocumentUpload';

const AdminPanel = () => {
  const { user, isAdmin } = useContext(AuthContext);
  const navigate = useNavigate();
  const [activeTab, setActiveTab] = useState('addCourse');
  const [selectedCourseId, setSelectedCourseId] = useState('');
  const [loading, setLoading] = useState(false);
  const [successMsg, setSuccessMsg] = useState('');
  const [errorMsg, setErrorMsg] = useState('');

  const [courseData, setCourseData] = useState({
    title: '',
    description: '',
    instructor: '',
    thumbnail: '',
    sections: [
      {
        title: '',
        lectures: [
          { title: '', description: '', videoUrl: '', videoDuration: 0 }
        ],
        quiz: null
      }
    ]
  });

  if (!user || !isAdmin) {
    navigate('/courses');
    return null;
  }

  const handleCourseChange = (e) => {
    const { name, value } = e.target;
    setCourseData(prev => ({ ...prev, [name]: value }));
  };

  const handleSectionChange = (sectionIndex, field, value) => {
    setCourseData(prev => {
      const newSections = [...prev.sections];
      newSections[sectionIndex][field] = value;
      return { ...prev, sections: newSections };
    });
  };

  const handleLectureChange = (sectionIndex, lectureIndex, field, value) => {
    setCourseData(prev => {
      const newSections = [...prev.sections];
      newSections[sectionIndex].lectures[lectureIndex][field] = value;
      return { ...prev, sections: newSections };
    });
  };

  const addSection = () => {
    setCourseData(prev => ({
      ...prev,
      sections: [
        ...prev.sections,
        {
          title: '',
          lectures: [{ title: '', description: '', videoUrl: '', videoDuration: 0 }],
          quiz: null
        }
      ]
    }));
  };

  const addLecture = (sectionIndex) => {
    setCourseData(prev => {
      const newSections = [...prev.sections];
      newSections[sectionIndex].lectures.push({
        title: '',
        description: '',
        videoUrl: '',
        videoDuration: 0
      });
      return { ...prev, sections: newSections };
    });
  };

  const removeSection = (sectionIndex) => {
    if (courseData.sections.length === 1) {
      setErrorMsg('At least one section is required');
      return;
    }
    setCourseData(prev => ({
      ...prev,
      sections: prev.sections.filter((_, idx) => idx !== sectionIndex)
    }));
  };

  const removeLecture = (sectionIndex, lectureIndex) => {
    if (courseData.sections[sectionIndex].lectures.length === 1) {
      setErrorMsg('At least one lecture per section is required');
      return;
    }
    setCourseData(prev => {
      const newSections = [...prev.sections];
      newSections[sectionIndex].lectures = newSections[sectionIndex].lectures.filter(
        (_, idx) => idx !== lectureIndex
      );
      return { ...prev, sections: newSections };
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setErrorMsg('');
    setSuccessMsg('');

    try {
      if (!courseData.title.trim()) {
        setErrorMsg('Course title is required');
        setLoading(false);
        return;
      }

      if (courseData.sections.some(s => !s.title.trim())) {
        setErrorMsg('All sections must have a title');
        setLoading(false);
        return;
      }

      if (courseData.sections.some(s => s.lectures.some(l => !l.title.trim()))) {
        setErrorMsg('All lectures must have a title');
        setLoading(false);
        return;
      }

      const response = await courseService.createCourseWithContent(courseData);
      setSuccessMsg(`Course "${response.data.course.title}" created successfully!`);

      setCourseData({
        title: '',
        description: '',
        instructor: '',
        thumbnail: '',
        sections: [
          {
            title: '',
            lectures: [
              { title: '', description: '', videoUrl: '', videoDuration: 0 }
            ],
            quiz: null
          }
        ]
      });

      setTimeout(() => setSuccessMsg(''), 5000);
    } catch (error) {
      setErrorMsg(error.response?.data?.message || 'Error creating course');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="container-fluid">
      <div className="row">
        <nav className="col-md-2 bg-dark p-3" style={{ minHeight: '100vh' }}>
          <h5 className="text-light mb-4">Admin Panel</h5>
          <ul className="navbar-nav">
            <li className="nav-item mb-3">
              <button 
                className={`nav-link text-light ${activeTab === 'addCourse' ? 'active' : ''}`}
                style={{background: 'none', border: 'none', cursor: 'pointer', textAlign: 'left'}}
                onClick={() => setActiveTab('addCourse')}
              >
                <i className="bi bi-plus-circle"></i> Add Course
              </button>
            </li>
            <li className="nav-item mb-3">
              <button 
                className={`nav-link text-light ${activeTab === 'documents' ? 'active' : ''}`}
                style={{background: 'none', border: 'none', cursor: 'pointer', textAlign: 'left'}}
                onClick={() => setActiveTab('documents')}
              >
                <i className="bi bi-file-upload"></i> Upload Documents
              </button>
            </li>
          </ul>
        </nav>
        <main className="col-md-10 p-4">
          {activeTab === 'addCourse' && (
            <>
              <div className="mb-4">
                <h2><i className="bi bi-plus-circle"></i> Add New Course</h2>
                <p className="text-muted">Create a course with sections, lessons and lectures</p>
              </div>

              {successMsg && <div className="alert alert-success">{successMsg}</div>}
              {errorMsg && <div className="alert alert-danger">{errorMsg}</div>}

              <form onSubmit={handleSubmit} className="card p-4">
                <div className="row mb-4">
                  <div className="col-md-6">
                    <label className="form-label fw-bold">Course Title *</label>
                    <input
                      type="text"
                      className="form-control"
                      name="title"
                      value={courseData.title}
                      onChange={handleCourseChange}
                      placeholder="e.g., Advanced React"
                      required
                    />
                  </div>
                  <div className="col-md-6">
                    <label className="form-label fw-bold">Instructor</label>
                    <input
                      type="text"
                      className="form-control"
                      name="instructor"
                      value={courseData.instructor}
                      onChange={handleCourseChange}
                      placeholder="Instructor name"
                    />
                  </div>
                </div>

                <div className="mb-4">
                  <label className="form-label fw-bold">Description</label>
                  <textarea
                    className="form-control"
                    name="description"
                    value={courseData.description}
                    onChange={handleCourseChange}
                    placeholder="Course description"
                    rows="3"
                  ></textarea>
                </div>

                <div className="mb-4">
                  <label className="form-label fw-bold">Thumbnail URL</label>
                  <input
                    type="text"
                    className="form-control"
                    name="thumbnail"
                    value={courseData.thumbnail}
                    onChange={handleCourseChange}
                    placeholder="https://example.com/image.jpg"
                  />
                </div>

                <hr />

                <h4 className="mb-3">Sections / Lessons</h4>
                {courseData.sections.map((section, sectionIndex) => (
                  <div key={sectionIndex} className="card mb-3 p-3 bg-light">
                    <div className="d-flex justify-content-between align-items-center mb-3">
                      <h5>Section {sectionIndex + 1}</h5>
                      {courseData.sections.length > 1 && (
                        <button
                          type="button"
                          className="btn btn-sm btn-danger"
                          onClick={() => removeSection(sectionIndex)}
                        >
                          Remove
                        </button>
                      )}
                    </div>

                    <div className="mb-3">
                      <label className="form-label fw-bold">Section Title *</label>
                      <input
                        type="text"
                        className="form-control"
                        value={section.title}
                        onChange={(e) => handleSectionChange(sectionIndex, 'title', e.target.value)}
                        placeholder="e.g., Basics, Advanced Concepts"
                        required
                      />
                    </div>

                    <div className="ms-3">
                      <h6 className="mb-2">Lectures</h6>
                      {section.lectures.map((lecture, lectureIndex) => (
                        <div key={lectureIndex} className="card mb-2 p-2 bg-white">
                          <div className="d-flex justify-content-between align-items-center mb-2">
                            <small className="fw-bold">Lecture {lectureIndex + 1}</small>
                            {section.lectures.length > 1 && (
                              <button
                                type="button"
                                className="btn btn-sm btn-warning"
                                onClick={() => removeLecture(sectionIndex, lectureIndex)}
                              >
                                Remove
                              </button>
                            )}
                          </div>

                          <div className="mb-2">
                            <label className="form-label small fw-bold">Title *</label>
                            <input
                              type="text"
                              className="form-control form-control-sm"
                              value={lecture.title}
                              onChange={(e) => handleLectureChange(sectionIndex, lectureIndex, 'title', e.target.value)}
                              placeholder="Lecture title"
                              required
                            />
                          </div>

                          <div className="mb-2">
                            <label className="form-label small fw-bold">Description</label>
                            <input
                              type="text"
                              className="form-control form-control-sm"
                              value={lecture.description}
                              onChange={(e) => handleLectureChange(sectionIndex, lectureIndex, 'description', e.target.value)}
                              placeholder="Brief description"
                            />
                          </div>

                          <div className="row mb-2">
                            <div className="col-md-8">
                              <label className="form-label small fw-bold">Video URL</label>
                              <input
                                type="text"
                                className="form-control form-control-sm"
                                value={lecture.videoUrl}
                                onChange={(e) => handleLectureChange(sectionIndex, lectureIndex, 'videoUrl', e.target.value)}
                                placeholder="https://youtube.com/embed/..."
                              />
                            </div>
                            <div className="col-md-4">
                              <label className="form-label small fw-bold">Duration (mins)</label>
                              <input
                                type="number"
                                className="form-control form-control-sm"
                                value={lecture.videoDuration}
                                onChange={(e) => handleLectureChange(sectionIndex, lectureIndex, 'videoDuration', parseInt(e.target.value) || 0)}
                                placeholder="45"
                                min="0"
                              />
                            </div>
                          </div>
                        </div>
                      ))}

                      <button
                        type="button"
                        className="btn btn-sm btn-info"
                        onClick={() => addLecture(sectionIndex)}
                      >
                        + Add Lecture
                      </button>
                    </div>
                  </div>
                ))}

                <button
                  type="button"
                  className="btn btn-secondary mb-4"
                  onClick={addSection}
                >
                  + Add Section
                </button>

                <hr />

                <button
                  type="submit"
                  className="btn btn-primary btn-lg w-100"
                  disabled={loading}
                >
                  {loading ? 'Creating Course...' : 'Create Course'}
                </button>
              </form>
            </>
          )}

          {activeTab === 'documents' && (
            <>
              <div className="mb-4">
                <h2><i className="bi bi-file-upload"></i> Document Management</h2>
                <p className="text-muted">Upload and manage course documents</p>
              </div>

              <div className="card p-4 mb-4">
                <div className="mb-4">
                  <label className="form-label fw-bold">Select Course</label>
                  <input
                    type="text"
                    className="form-control"
                    placeholder="Enter Course ID"
                    value={selectedCourseId}
                    onChange={(e) => setSelectedCourseId(e.target.value)}
                  />
                  <small className="text-muted d-block mt-2">
                    Enter the MongoDB Course ID to upload documents for that course
                  </small>
                </div>

                {selectedCourseId && (
                  <DocumentUpload courseId={selectedCourseId} onUploadSuccess={() => {}} />
                )}

                {!selectedCourseId && (
                  <div className="alert alert-info">
                    <i className="bi bi-info-circle"></i> Please enter a Course ID to begin uploading documents
                  </div>
                )}
              </div>
            </>
          )}
        </main>
      </div>
    </div>
  );
};

export default AdminPanel;
