import React, { useContext, useState } from 'react';
import { useParams, useNavigate, Link } from 'react-router-dom';
import { AuthContext } from '../context/AuthContext';
import CourseSections from '../components/CourseSections';
import DocumentUpload from '../components/DocumentUpload';
import DocumentList from '../components/DocumentList';

const CourseDetail = () => {
  const { id } = useParams();
  const { user } = useContext(AuthContext);
  const [activeTab, setActiveTab] = useState('lectures');
  const [refreshDocs, setRefreshDocs] = useState(0);
  const navigate = useNavigate();

  if (!user) {
    navigate('/login');
    return null;
  }

  const handleUploadSuccess = () => {
    setRefreshDocs(prev => prev + 1);
  };

  return (
    <div className="container-fluid">
      <div className="row">
        <nav className="col-md-2 bg-dark p-3" style={{ minHeight: '100vh' }}>
          <ul className="navbar-nav">
            <li className="nav-item mb-3">
              <Link to="/courses" className="nav-link text-light">
                <i className="bi bi-house"></i> My Courses
              </Link>
            </li>
            <li className="nav-item mb-2">
              <button 
                className={`nav-link ${activeTab === 'lectures' ? 'active' : ''}`}
                onClick={() => setActiveTab('lectures')}
                style={{background: 'none', border: 'none', cursor: 'pointer'}}
              >
                <i className="bi bi-play-circle"></i> Lectures
              </button>
            </li>
            <li className="nav-item">
              <button 
                className={`nav-link ${activeTab === 'documents' ? 'active' : ''}`}
                onClick={() => setActiveTab('documents')}
                style={{background: 'none', border: 'none', cursor: 'pointer'}}
              >
                <i className="bi bi-file-text"></i> Documents
              </button>
            </li>
          </ul>
        </nav>
        <main className="col-md-10 p-4">
          {activeTab === 'lectures' && <CourseSections courseId={id} />}
          {activeTab === 'documents' && (
            <div>
              <DocumentUpload courseId={id} onUploadSuccess={handleUploadSuccess} />
              <DocumentList courseId={id} key={refreshDocs} />
            </div>
          )}
        </main>
      </div>
    </div>
  );
};

export default CourseDetail;
